INSERT INTO "AnalysisItem" (id, "groupId", name, "nameEn", description, "sportType", "displayOrder", "updatedAt") VALUES (22, (SELECT id FROM "AnalysisGroup" WHERE name='旋轉運動-軸轉(Pivot)' AND "sportType"='snowboard' LIMIT 1), '魚游 ( Fish Turns / Roller Coaster Turns )', NULL, '想像雪板像隻魚一樣頭轉到那一個方向尾巴永遠會跟上去，可以加強前腳先轉後腳跟上的感覺讓雪板轉動更靈敏', 'snowboard', 22, NOW()) ON CONFLICT (id) DO UPDATE SET "groupId"=EXCLUDED."groupId", name=EXCLUDED.name, description=EXCLUDED.description, "sportType"=EXCLUDED."sportType", "displayOrder"=EXCLUDED."displayOrder", "updatedAt"=NOW();
