INSERT INTO "AnalysisItem" (id, "groupId", name, "nameEn", description, "sportType", "displayOrder", "updatedAt") VALUES (29, (SELECT id FROM "AnalysisGroup" WHERE name='橫向運動-立刃(Edging)' AND "sportType"='snowboard' LIMIT 1), '緩坡立刃練習 ( Static Edging Exercise )', NULL, '通過在緩坡上以單腳穿雪板學習橫板立刃正確姿式，後刃橫板像坐椅一樣、前刃橫板是上身與髖骨挺直只有膝蓋腳踝往下彎，加強建立肌肉記憶', 'snowboard', 29, NOW()) ON CONFLICT (id) DO UPDATE SET "groupId"=EXCLUDED."groupId", name=EXCLUDED.name, description=EXCLUDED.description, "sportType"=EXCLUDED."sportType", "displayOrder"=EXCLUDED."displayOrder", "updatedAt"=NOW();
INSERT INTO "AnalysisItem" (id, "groupId", name, "nameEn", description, "sportType", "displayOrder", "updatedAt") VALUES (31, (SELECT id FROM "AnalysisGroup" WHERE name='橫向運動-立刃(Edging)' AND "sportType"='snowboard' LIMIT 1), '飲料在肩膀 ( Drinks on Shoulders )', NULL, '藉由想像滑行時有飲料在雙邊的肩膀，為了不打翻飲料而加強下半身的彎曲，讓學生可以在滑行加強下半身的運用而保持上身穩定', 'snowboard', 31, NOW()) ON CONFLICT (id) DO UPDATE SET "groupId"=EXCLUDED."groupId", name=EXCLUDED.name, description=EXCLUDED.description, "sportType"=EXCLUDED."sportType", "displayOrder"=EXCLUDED."displayOrder", "updatedAt"=NOW();
INSERT INTO "AnalysisItem" (id, "groupId", name, "nameEn", description, "sportType", "displayOrder", "updatedAt") VALUES (32, (SELECT id FROM "AnalysisGroup" WHERE name='橫向運動-立刃(Edging)' AND "sportType"='snowboard' LIMIT 1), '鞋水倒出 ( Pour Water from the Boots )', NULL, '割雪滑行時想像雪鞋裡都是水，需要在換刃時把鞋子裡的水倒出，藉由這個訓練可以增加身體傾斜與曲弓程度，讓割雪滑行的速度更快更穩定', 'snowboard', 32, NOW()) ON CONFLICT (id) DO UPDATE SET "groupId"=EXCLUDED."groupId", name=EXCLUDED.name, description=EXCLUDED.description, "sportType"=EXCLUDED."sportType", "displayOrder"=EXCLUDED."displayOrder", "updatedAt"=NOW();
