INSERT INTO "AnalysisItem" (id, "groupId", name, "nameEn", description, "sportType", "displayOrder", "updatedAt") VALUES (57, (SELECT id FROM "AnalysisGroup" WHERE name='站姿與平衡(Position & Balance)' AND "sportType"='snowboard' LIMIT 1), '橫板下滑跳180 ( Side Slipping 180 Jumps )', NULL, '通過橫板向山下滑行時跳躍180度，從後刃跳到前刃與前刃跳到後刃，讓學生了解滑行時平衡姿式的重要性', 'snowboard', 57, NOW()) ON CONFLICT (id) DO UPDATE SET "groupId"=EXCLUDED."groupId", name=EXCLUDED.name, description=EXCLUDED.description, "sportType"=EXCLUDED."sportType", "displayOrder"=EXCLUDED."displayOrder", "updatedAt"=NOW();
INSERT INTO "AnalysisItem" (id, "groupId", name, "nameEn", description, "sportType", "displayOrder", "updatedAt") VALUES (58, (SELECT id FROM "AnalysisGroup" WHERE name='站姿與平衡(Position & Balance)' AND "sportType"='snowboard' LIMIT 1), '平地跳躍 ( Static Jump / “T” Stance )', NULL, '通過平地雙腳跳躍，在著地時雙腳一起著地且身體保持平衡，可以讓學生感受這著地的平衡感跟滑行時一樣', 'snowboard', 58, NOW()) ON CONFLICT (id) DO UPDATE SET "groupId"=EXCLUDED."groupId", name=EXCLUDED.name, description=EXCLUDED.description, "sportType"=EXCLUDED."sportType", "displayOrder"=EXCLUDED."displayOrder", "updatedAt"=NOW();
INSERT INTO "AnalysisItem" (id, "groupId", name, "nameEn", description, "sportType", "displayOrder", "updatedAt") VALUES (59, (SELECT id FROM "AnalysisGroup" WHERE name='站姿與平衡(Position & Balance)' AND "sportType"='snowboard' LIMIT 1), '反腳滑行 ( Switch Riding )', NULL, '藉由訓練反腳滑行 ，可以讓學生往更多的平地花式滑行技巧學習且會使正腳滑行更穩定', 'snowboard', 59, NOW()) ON CONFLICT (id) DO UPDATE SET "groupId"=EXCLUDED."groupId", name=EXCLUDED.name, description=EXCLUDED.description, "sportType"=EXCLUDED."sportType", "displayOrder"=EXCLUDED."displayOrder", "updatedAt"=NOW();
INSERT INTO "AnalysisItem" (id, "groupId", name, "nameEn", description, "sportType", "displayOrder", "updatedAt") VALUES (60, (SELECT id FROM "AnalysisGroup" WHERE name='站姿與平衡(Position & Balance)' AND "sportType"='snowboard' LIMIT 1), '非自然起跳 / 滑行 ( Toe to Toe / Heel to Heel )', NULL, '一邊鋼邊滑行時旋轉或跳躍旋轉180度至同一邊鋼邊反腳繼續滑行 ，由這高難度動作可以挑戰學生的滑行平衡度以及兩邊的刻滑程度', 'snowboard', 60, NOW()) ON CONFLICT (id) DO UPDATE SET "groupId"=EXCLUDED."groupId", name=EXCLUDED.name, description=EXCLUDED.description, "sportType"=EXCLUDED."sportType", "displayOrder"=EXCLUDED."displayOrder", "updatedAt"=NOW();
INSERT INTO "AnalysisItem" (id, "groupId", name, "nameEn", description, "sportType", "displayOrder", "updatedAt") VALUES (79, (SELECT id FROM "AnalysisGroup" WHERE name='給予壓力大小(Pressure or Power)' AND "sportType"='snowboard' LIMIT 1), '靜態屈身減壓轉彎 ( Static Down Un-Weighting )', NULL, '轉彎程序與引身減壓轉彎相反，在轉彎前是屈身入彎減少壓力，轉彎時是伸展去讓雪板抓地，屬於較高階的滑行，讓學生可以應付更多不同的地形', 'snowboard', 79, NOW()) ON CONFLICT (id) DO UPDATE SET "groupId"=EXCLUDED."groupId", name=EXCLUDED.name, description=EXCLUDED.description, "sportType"=EXCLUDED."sportType", "displayOrder"=EXCLUDED."displayOrder", "updatedAt"=NOW();
INSERT INTO "AnalysisItem" (id, "groupId", name, "nameEn", description, "sportType", "displayOrder", "updatedAt") VALUES (80, (SELECT id FROM "AnalysisGroup" WHERE name='時間(Timing & Coordination)' AND "sportType"='snowboard' LIMIT 1), '公園滑行 / 障礙滑行技巧 ( Slopestyle Run )', NULL, '在滑行公園前先把要滑行的路線計畫好，可以讓學生練習滑行前的地形觀察與滑行計畫', 'snowboard', 80, NOW()) ON CONFLICT (id) DO UPDATE SET "groupId"=EXCLUDED."groupId", name=EXCLUDED.name, description=EXCLUDED.description, "sportType"=EXCLUDED."sportType", "displayOrder"=EXCLUDED."displayOrder", "updatedAt"=NOW();
INSERT INTO "AnalysisItem" (id, "groupId", name, "nameEn", description, "sportType", "displayOrder", "updatedAt") VALUES (91, (SELECT id FROM "AnalysisGroup" WHERE name='旋轉運動-軸轉(Pivot)' AND "sportType"='snowboard' LIMIT 1), '楷梯滑行 ( Garland / “Chicken-Out” Turns / Fall-Line Pivot Exercise )', NULL, '藉由楷梯式滑行，學生可以學習如何讓雪板轉向讓雪板轉至下坡再轉回剛剛的橫板，是S turn前非常重要的一環', 'snowboard', 91, NOW()) ON CONFLICT (id) DO UPDATE SET "groupId"=EXCLUDED."groupId", name=EXCLUDED.name, description=EXCLUDED.description, "sportType"=EXCLUDED."sportType", "displayOrder"=EXCLUDED."displayOrder", "updatedAt"=NOW();
INSERT INTO "AnalysisItem" (id, "groupId", name, "nameEn", description, "sportType", "displayOrder", "updatedAt") VALUES (94, (SELECT id FROM "AnalysisGroup" WHERE name='旋轉運動-軸轉(Pivot)' AND "sportType"='snowboard' LIMIT 1), '船外機 ( Motor Boat )', NULL, '想像操作船外機一樣，當雪板要從後刃轉到前刃時，後手需要將船外機的油門推過去後刃方向且船頭與肩膀轉到前刃方向，可以讓身體旋轉達到一致性', 'snowboard', 94, NOW()) ON CONFLICT (id) DO UPDATE SET "groupId"=EXCLUDED."groupId", name=EXCLUDED.name, description=EXCLUDED.description, "sportType"=EXCLUDED."sportType", "displayOrder"=EXCLUDED."displayOrder", "updatedAt"=NOW();
INSERT INTO "AnalysisItem" (id, "groupId", name, "nameEn", description, "sportType", "displayOrder", "updatedAt") VALUES (97, (SELECT id FROM "AnalysisGroup" WHERE name='旋轉運動-軸轉(Pivot)' AND "sportType"='snowboard' LIMIT 1), '靜態上身旋轉練習 ( Static Rotation Exercise )', NULL, '在平地上不穿雪板，由上半身先旋轉去帶動下半身旋轉，可加強平地花式技巧或日式割雪滑行', 'snowboard', 97, NOW()) ON CONFLICT (id) DO UPDATE SET "groupId"=EXCLUDED."groupId", name=EXCLUDED.name, description=EXCLUDED.description, "sportType"=EXCLUDED."sportType", "displayOrder"=EXCLUDED."displayOrder", "updatedAt"=NOW();
INSERT INTO "AnalysisItem" (id, "groupId", name, "nameEn", description, "sportType", "displayOrder", "updatedAt") VALUES (98, (SELECT id FROM "AnalysisGroup" WHERE name='旋轉運動-軸轉(Pivot)' AND "sportType"='snowboard' LIMIT 1), 'X型轉體 ( X-Turns )', NULL, '在平地上穿雪板，利用小跳躍同時轉動身體與雪板一些角度再跳轉另一邊，完成後雪地上會有X的字型，可以讓學生感受身體帶著雪板轉動的感覺', 'snowboard', 98, NOW()) ON CONFLICT (id) DO UPDATE SET "groupId"=EXCLUDED."groupId", name=EXCLUDED.name, description=EXCLUDED.description, "sportType"=EXCLUDED."sportType", "displayOrder"=EXCLUDED."displayOrder", "updatedAt"=NOW();
INSERT INTO "AnalysisItem" (id, "groupId", name, "nameEn", description, "sportType", "displayOrder", "updatedAt") VALUES (100, (SELECT id FROM "AnalysisGroup" WHERE name='給予壓力大小(Pressure or Power)' AND "sportType"='snowboard' LIMIT 1), '地形小跳躍 ( Small Straight Airs )', NULL, '不平整的地形上，在突起的頂點伸展開做小跳躍，可以讓學生在雪地滑行時利用不一樣的地形做出適合的滑行技巧', 'snowboard', 100, NOW()) ON CONFLICT (id) DO UPDATE SET "groupId"=EXCLUDED."groupId", name=EXCLUDED.name, description=EXCLUDED.description, "sportType"=EXCLUDED."sportType", "displayOrder"=EXCLUDED."displayOrder", "updatedAt"=NOW();
INSERT INTO "AnalysisItem" (id, "groupId", name, "nameEn", description, "sportType", "displayOrder", "updatedAt") VALUES (102, (SELECT id FROM "AnalysisGroup" WHERE name='橫向運動-立刃(Edging)' AND "sportType"='snowboard' LIMIT 1), '斜坡橫板跳躍 ( Stop-N-Hop’s )', NULL, '在斜坡上橫板以單邊的鋼邊站著保持平衡，再以單鋼邊小跳躍至上坡和下坡，在著地時保持單鋼邊著地平衡再練習另一邊，可以增加在鋼邊控制上的運用和滑行的平衡姿式', 'snowboard', 102, NOW()) ON CONFLICT (id) DO UPDATE SET "groupId"=EXCLUDED."groupId", name=EXCLUDED.name, description=EXCLUDED.description, "sportType"=EXCLUDED."sportType", "displayOrder"=EXCLUDED."displayOrder", "updatedAt"=NOW();
INSERT INTO "AnalysisItem" (id, "groupId", name, "nameEn", description, "sportType", "displayOrder", "updatedAt") VALUES (106, (SELECT id FROM "AnalysisGroup" WHERE name='橫向運動-立刃(Edging)' AND "sportType"='snowboard' LIMIT 1), '割雪跳躍換邊 ( Hop-Carved Turns )', NULL, '在割雪滑行時，以跳躍在空中換刃著地同時繼續滑行下個彎，已經會初階割雪滑行後可以藉由這個訓練增加割雪滑行的穩定度', 'snowboard', 106, NOW()) ON CONFLICT (id) DO UPDATE SET "groupId"=EXCLUDED."groupId", name=EXCLUDED.name, description=EXCLUDED.description, "sportType"=EXCLUDED."sportType", "displayOrder"=EXCLUDED."displayOrder", "updatedAt"=NOW();
INSERT INTO "AnalysisItem" (id, "groupId", name, "nameEn", description, "sportType", "displayOrder", "updatedAt") VALUES (110, (SELECT id FROM "AnalysisGroup" WHERE name='橫向運動-立刃(Edging)' AND "sportType"='snowboard' LIMIT 1), '橫向滑行換刃 ( Inverse Traverse )', NULL, '在橫向滑行以一定的速度滑行楷梯形狀，並在橫滑的時候藉由一點回山滑行的離心力換刃(山下刃)且保持橫滑再重復練習，加強換刃的速度與提早換刃的時機', 'snowboard', 110, NOW()) ON CONFLICT (id) DO UPDATE SET "groupId"=EXCLUDED."groupId", name=EXCLUDED.name, description=EXCLUDED.description, "sportType"=EXCLUDED."sportType", "displayOrder"=EXCLUDED."displayOrder", "updatedAt"=NOW();
INSERT INTO "AnalysisItem" (id, "groupId", name, "nameEn", description, "sportType", "displayOrder", "updatedAt") VALUES (114, (SELECT id FROM "AnalysisGroup" WHERE name='橫向運動-立刃(Edging)' AND "sportType"='snowboard' LIMIT 1), '立刃滑行 ( Sidecut Turns )', NULL, '在緩坡滑行入前刃彎時微微的把身體傾向前刃讓刃立起來在雪地畫出細線彎結束時身體回正，下個後刃彎傾向後刃，讓學生熟悉以鋼邊弧度滑行與身體傾斜程度', 'snowboard', 114, NOW()) ON CONFLICT (id) DO UPDATE SET "groupId"=EXCLUDED."groupId", name=EXCLUDED.name, description=EXCLUDED.description, "sportType"=EXCLUDED."sportType", "displayOrder"=EXCLUDED."displayOrder", "updatedAt"=NOW();
INSERT INTO "AnalysisItem" (id, "groupId", name, "nameEn", description, "sportType", "displayOrder", "updatedAt") VALUES (115, (SELECT id FROM "AnalysisGroup" WHERE name='橫向運動-立刃(Edging)' AND "sportType"='snowboard' LIMIT 1), '拍打板底 ( Slap Base )', NULL, '在滑行轉彎換刃時，從前刃換到後刃中間藉由腳踝力量把雪板板底拍向雪地，增加換刃的抓地感使轉彎更穩定', 'snowboard', 115, NOW()) ON CONFLICT (id) DO UPDATE SET "groupId"=EXCLUDED."groupId", name=EXCLUDED.name, description=EXCLUDED.description, "sportType"=EXCLUDED."sportType", "displayOrder"=EXCLUDED."displayOrder", "updatedAt"=NOW();
INSERT INTO "AnalysisItem" (id, "groupId", name, "nameEn", description, "sportType", "displayOrder", "updatedAt") VALUES (116, (SELECT id FROM "AnalysisGroup" WHERE name='橫向運動-立刃(Edging)' AND "sportType"='snowboard' LIMIT 1), '減速 ( Speed Checks )', NULL, '在緩坡直板向山下滑行，速度變快時藉由下半身把後或前刃轉至與滾落線成垂直且微微立起刃讓速度減慢然後再回到直板滑行', 'snowboard', 116, NOW()) ON CONFLICT (id) DO UPDATE SET "groupId"=EXCLUDED."groupId", name=EXCLUDED.name, description=EXCLUDED.description, "sportType"=EXCLUDED."sportType", "displayOrder"=EXCLUDED."displayOrder", "updatedAt"=NOW();
INSERT INTO "AnalysisItem" (id, "groupId", name, "nameEn", description, "sportType", "displayOrder", "updatedAt") VALUES (118, (SELECT id FROM "AnalysisGroup" WHERE name='橫向運動-立刃(Edging)' AND "sportType"='snowboard' LIMIT 1), '轉彎秀板底 ( Sponsor Turns )', NULL, '刻雪滑行在轉彎時利用傾斜、曲弓及提早換刃讓立刃角度高到可以被周圍的人看到板底，可以增加立刃抓地力使刻雪滑行更快', 'snowboard', 118, NOW()) ON CONFLICT (id) DO UPDATE SET "groupId"=EXCLUDED."groupId", name=EXCLUDED.name, description=EXCLUDED.description, "sportType"=EXCLUDED."sportType", "displayOrder"=EXCLUDED."displayOrder", "updatedAt"=NOW();
INSERT INTO "AnalysisItem" (id, "groupId", name, "nameEn", description, "sportType", "displayOrder", "updatedAt") VALUES (122, (SELECT id FROM "AnalysisGroup" WHERE name='給予壓力大小(Pressure or Power)' AND "sportType"='snowboard' LIMIT 1), '海豚式轉彎 ( Dolphin Turns )', NULL, '藉由模仿海豚一樣的跳躍方式像oille，以板尾彈起後在空中換刃再以另一個鋼邊與前腳著地重復此動作，學生可以學到如何獨立使用前、後腳去控制地形的壓力', 'snowboard', 122, NOW()) ON CONFLICT (id) DO UPDATE SET "groupId"=EXCLUDED."groupId", name=EXCLUDED.name, description=EXCLUDED.description, "sportType"=EXCLUDED."sportType", "displayOrder"=EXCLUDED."displayOrder", "updatedAt"=NOW();
INSERT INTO "AnalysisItem" (id, "groupId", name, "nameEn", description, "sportType", "displayOrder", "updatedAt") VALUES (125, (SELECT id FROM "AnalysisGroup" WHERE name='給予壓力大小(Pressure or Power)' AND "sportType"='snowboard' LIMIT 1), '直滑急煞 ( Fall-Line Stops )', NULL, '藉由直滑下山的橫板緊急煞車，可以學習到在有速度的狀況下如何以我們的下半身的彎曲與旋轉達到橫板仍保持上半身不動，然後慢慢將下身伸展開增加對雪地的壓力而停止', 'snowboard', 125, NOW()) ON CONFLICT (id) DO UPDATE SET "groupId"=EXCLUDED."groupId", name=EXCLUDED.name, description=EXCLUDED.description, "sportType"=EXCLUDED."sportType", "displayOrder"=EXCLUDED."displayOrder", "updatedAt"=NOW();
INSERT INTO "AnalysisItem" (id, "groupId", name, "nameEn", description, "sportType", "displayOrder", "updatedAt") VALUES (129, (SELECT id FROM "AnalysisGroup" WHERE name='給予壓力大小(Pressure or Power)' AND "sportType"='snowboard' LIMIT 1), '板尾 / 板頭起跳 ( Ollies / Nollies )', NULL, '板尾地跳，彎曲下半身同時將重心移到後腳，以板尾跳躍在空在板子回正再以雙腳一起著地，板頭起跳是以相反的方式，藉由這訓練可以學習到單腳控制壓力的能力以及對雪地沖擊力的吸收', 'snowboard', 129, NOW()) ON CONFLICT (id) DO UPDATE SET "groupId"=EXCLUDED."groupId", name=EXCLUDED.name, description=EXCLUDED.description, "sportType"=EXCLUDED."sportType", "displayOrder"=EXCLUDED."displayOrder", "updatedAt"=NOW();
INSERT INTO "AnalysisItem" (id, "groupId", name, "nameEn", description, "sportType", "displayOrder", "updatedAt") VALUES (140, (SELECT id FROM "AnalysisGroup" WHERE name='時間(Timing & Coordination)' AND "sportType"='snowboard' LIMIT 1), '跟鏡頭滑行 ( Follow-Cam Run )', NULL, '主要用在公園滑行，讓學生2人一隊，滑在前面的人可以設定要滑什麼樣的路線，後面的人想像在跟拍而滑行一樣路線，讓後面的學生可以學習前面滑者的滑行時機與程序', 'snowboard', 140, NOW()) ON CONFLICT (id) DO UPDATE SET "groupId"=EXCLUDED."groupId", name=EXCLUDED.name, description=EXCLUDED.description, "sportType"=EXCLUDED."sportType", "displayOrder"=EXCLUDED."displayOrder", "updatedAt"=NOW();
